import { Component, OnInit } from '@angular/core';
@Component({
  selector: 'app-navbar',
  templateUrl: './navbar.component.html',
  styleUrls: ['./navbar.component.css'],
})
export class NavbarComponent implements OnInit {
  constructor() {}

  ngOnInit() {
    // Select all elements with class 'nav-link'
    const links = document.querySelectorAll('.nav-link');

    // For each link, attach a click event listener
    links.forEach((link) => {
      link.addEventListener('click', () => {
        // Remove 'active' class from all links
        links.forEach((l) => l.classList.remove('active'));

        // Add 'active' class to the clicked link
        link.classList.add('active');
      });
    });
  }
}
<nav class="nav justify-content-center">
  <ul class="nav justify-content-center">
    <li class="nav-item" routerLinkActive="active">
      <a class="nav-link active" routerLink="/" fragment="about">About</a>
    </li>
    <li class="nav-item" routerLinkActive="active">
      <a class="nav-link" routerLink="/" fragment="projects">Projects</a>
    </li>
    <li class="nav-item" routerLinkActive="active">
      <a class="nav-link" routerLink="/" fragment="skills">Skills</a>
    </li>
    <li class="nav-item" routerLinkActive="active">
      <a class="nav-link" routerLink="/" fragment="contact">Contact</a>
    </li>
  </ul>
  <i class="fas fa-bars menu"></i>
</nav>
