import { Component, OnInit, Renderer2 } from '@angular/core';
@Component({
  selector: 'app-navbar',
  templateUrl: './navbar.component.html',
  styleUrls: ['./navbar.component.css'],
})
export class NavbarComponent implements OnInit {
  constructor(private renderer: Renderer2) {}

  ngOnInit() {
    // Select all elements with class 'nav-link'
    const links = document.querySelectorAll('.nav-link');

    // For each link, attach a click event listener
    links.forEach((link) => {
      link.addEventListener('click', () => {
        // Remove 'active' class from all links
        links.forEach((l) => l.classList.remove('active'));

        // Add 'active' class to the clicked link
        link.classList.add('active');
      });
    });
  }

  toggleNav() {
    const menu = document.querySelector('.menu');
    if (menu) {
      this.renderer.setStyle(menu, 'transform', `rotate(30eg)`);
    }
    document.querySelector('.nav')?.classList.toggle('show');
  }
}
<nav>
  <ul class="nav justify-content-center">
    <li class="nav-item" routerLinkActive="active">
      <a
        class="nav-link active"
        routerLink="/"
        fragment="about"
        (click)="toggleNav()"
        >About</a
      >
    </li>
    <li class="nav-item" routerLinkActive="active">
      <a
        class="nav-link"
        routerLink="/"
        fragment="projects"
        (click)="toggleNav()"
        >Projects</a
      >
    </li>
    <li class="nav-item" routerLinkActive="active">
      <a class="nav-link" routerLink="/" fragment="skills" (click)="toggleNav()"
        >Skills</a
      >
    </li>
    <li class="nav-item" routerLinkActive="active">
      <a
        class="nav-link"
        routerLink="/"
        fragment="contact"
        (click)="toggleNav()"
        >Contact</a
      >
    </li>
  </ul>
  <div class="menu">
    <i class="fas fa-bars menu" (click)="toggleNav()"></i>
  </div>
</nav>
